import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faUser, faCreditCard } from "@fortawesome/free-solid-svg-icons";
import { Button, Nav, NavItem, Container } from "reactstrap";
import "./NavBar.css"; // Importamos el archivo de estilos

export default function NavBar() {
  const [isMobile, setIsMobile] = useState(window.innerWidth <= 1100);

  // Manejar el cambio de tamaño de la ventana
  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth <= 1100);
    };

    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  return (
    <Container fluid>
      {isMobile ? (
        <div>
          
        </div>
      ) : (
        
        <Nav className={`navbar ${isMobile ? "horizontal-nav" : "vertical-nav"}`}>
      <div className={`${isMobile ? "h-nav-items-container" : "v-nav-items-container"}`}>
      <NavItem className="nav-title mb-4">
        <h5>Farmacia Alto Palermo</h5>
      </NavItem>
        <NavItem className="mb-4">
          <Link to="/home">
            <Button className="first-button nav-button">
              <FontAwesomeIcon icon={faUser} /> Clientes
            </Button>
          </Link>
        </NavItem>
        <NavItem>
          <Link to="/obras-sociales">
            <Button className="first-button nav-button">
              <FontAwesomeIcon icon={faCreditCard} /> Obras Sociales
            </Button>
          </Link>
        </NavItem>
      </div>
    </Nav>
      )}
    
    </Container>

  );
}
