const initialState = {
  clientes: [],
  allClientes: [],
};

export default function rootReducer(state=initialState, action) {
  switch(action.type) {
    case 'GET_CLIENTES':
      return{
        ...state,
        clientes: action.payload,
        allClientes: action.payload,
      }
    
    default:
      return state;
      
  }

}
