import React from "react";
import { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getObrasSociales, orderByName, deleteObraSocial } from "../redux/actions";
import { Link } from 'react-router-dom';
import Paginado from "./Paginado";
import SearchBar from "./SearchBar";



export default function ObrasSociales(){
  const dispatch = useDispatch();
  const allObrasSociales = useSelector((state) => state.ObrasSociales);
  // const error = useSelector((state) => state.error);
  // const [orden, setOrden] = useState('');

   //PAGINADO
   const [currentPage, setCurrentPage] = useState(1);
   const [obrasSocialesPorPagina, /*setObrasSocialesPorPagina*/] = useState(6);
   const indexUltimaObraSocial = currentPage * obrasSocialesPorPagina;
   const indexPrimerCliente = indexUltimaObraSocial - obrasSocialesPorPagina;
   const currentObrasSociales = allObrasSociales.slice(indexPrimerCliente, indexUltimaObraSocial);
 
   const paginado = (pageNumber) => {
     setCurrentPage(pageNumber);
   };
   //FINAL PAGINADO

   useEffect(() => {
    dispatch(getObrasSociales());
  }, [dispatch]);

  function handleClick(e){
    e.preventDefault();
    dispatch(getObrasSociales());
  };


  return (
    <div>
      <Link to={'/home'}><button>volver</button></Link>
      <h1>OBRAS SOCIALES</h1>
      <button onClick={ e => {handleClick(e)} }>
        volver a cargar todas las obras sociales
      </button>
    </div>
  )
}
