const initialState = {
  clientes: [],
};

export default function rootReducer(state=initialState, action) {
  switch(action.type) {
    case 'GET_CLIENTES':
      return{
        ...state,
        clientes: action.payload,
      }
    
    default:
      break;
  }

}
