import React, {useState, useEffect} from "react";


export default function CalculadoraPuntos(){
  const [puntosSumados, setPuntosSumados] = useState(0);
  const [puntosTotal, setPuntosTotal] = useState(0);
  const [input, setInput] = useState({
    dineroPorPunto: 0,
    puntosActuales: 0,
    nuevaCompra: 0
  })
  

  function handleInputChange(e){
    const { name, value} = e.target;
    setInput({
      ...input,
      [name]: parseFloat(value) || 0,
    });
  }

  useEffect(() => {
    const { dineroPorPunto, puntosActuales, nuevaCompra } = input;
    const nuevosPuntos = dineroPorPunto > 0 ? Math.floor(nuevaCompra / dineroPorPunto) : 0;
    
    setPuntosSumados(nuevosPuntos);
    setPuntosTotal(puntosActuales + nuevosPuntos);
  }, [input]);
  
  
  
  return (
    <div>
      <h1>CALCULAR PUNTOS</h1>
      <div>
        <label>1 Punto = $</label>
        <input type="number" name="dineroPorPunto" value={input.dineroPorPunto} onChange={handleInputChange}></input>
      </div>
      <div>
        <label>Puntos Actuales: </label>
        <input type="number" name="puntosActuales" value={input.puntosActuales} onChange={handleInputChange}></input>
      </div>
      <div>
        <label>Nueva Compra: </label>
        <input type="number" name="nuevaCompra" value={input.nuevaCompra} onChange={handleInputChange}></input>
      </div>
      <div>
        <label>Puntos Sumados: </label>
        <span>{puntosSumados}</span>
      </div>
      <div>
        <label>Puntos Total: </label>
        <spa>{puntosTotal}</spa>
      </div>
    </div>
  )
}
