import React from "react";
import { useEffect } from "react";
import { Link, useParams } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { getDetail } from "../redux/actions";

export default function Detail(){
  const dispatch = useDispatch();
  const { id } = useParams();

  useEffect(() => {
    dispatch(getDetail(id));
  }, [dispatch, id])

  const miCliente = useSelector((state) => state.detail);

  return (
    <div>
      {
        miCliente.length > 0 ?
        <div>
          <h1>Apellido: {miCliente[0].nombre}</h1>
          <h1>Apellido: {miCliente[0].apellido}</h1>
          <h1>Puntos: {miCliente[0].puntos}</h1>
          <h1>DNI: {miCliente[0].dni}</h1>
          <h1>Numero de Afiliado: {miCliente[0].numeroDeAfiliado}</h1>
          <h1>Dirección: {miCliente[0].direccion}</h1>
          <h1>Teléfono: {miCliente[0].telefono}</h1>
          <h1>Obras Sociales: {miCliente[0].obraSocials.map(el => el.nombre + (' - '))}</h1>
        </div> :
        <p>No se encontró el cliente</p>
      }
      <Link to={'/home'}><button>volver</button></Link>

    </div>
  )

}
