import React, {useState} from "react";
import {Link, useNavigate} from 'react-router-dom';
import { useDispatch } from "react-redux";
import { postObraSocial } from "../redux/actions";
import { Form, FormGroup, Label, Col, Input, Button, Container} from 'reactstrap';


function validate(input){
  let errors = {};
  if(!input.nombre) {
    errors.nombre = "se requiere un nombre";
  }
  return errors;
}

export default function ObraSocialCreate(){

  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [errors, setErrors] = useState({});
  const [input, setInput] = useState({
    nombre: '',
  });

  function handleChange(e){
    setInput({
      ...input,
      [e.target.name] : e.target.value
    })
    setErrors(validate({
      ...input,
      [e.target.name] : e.target.value
    }));
    console.log(input);
  };

  function handleSubmit(e){
    e.preventDefault();
    console.log(input);
    dispatch(postObraSocial(input));
    alert('obra social creada con éxito!');
    setInput({
      nombre: '',
    });
    navigate('/obras-sociales');
  }




  return (
    <Container>
      <Link to={'/home'}><Button color="primary">volver</Button></Link>
      <h1>Agregar Nueva Obra Social</h1>

      
      <Form onSubmit={(e) => handleSubmit(e)}>
    <FormGroup row>

        <div>
          <label>Nombre*: </label>
          <input type="text" value={input.nombre} name='nombre' onChange={(e) => handleChange(e)}/>
          {errors.nombre && (<p>{errors.nombre}</p>)}
        </div>
        <br/>
        {Object.keys(errors).length?
        <Button color="success" type="submit" disabled>agregar obra social</Button>
        :
        <Button color="success" type="submit">agregar obra social</Button>
      }    
      </FormGroup>
        </Form>
    </Container>
  )
}
