import React from "react";
import { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getClientes, orderByName, deleteCliente } from "../redux/actions";
import { Link } from 'react-router-dom';
import Card from "./Card";
import Paginado from "./Paginado";
import SearchBar from "./SearchBar";

export default function Home(){

  const dispatch = useDispatch();
  const allClientes = useSelector((state) => state.clientes);
  const error = useSelector((state) => state.error);
  const [orden, setOrden] = useState('');
  //PAGINADO
  const [currentPage, setCurrentPage] = useState(1);
  const [clientesPorPagina, /*setClientesPorPagina*/] = useState(6);
  const indexUltimoCliente = currentPage * clientesPorPagina;
  const indexPrimerCliente = indexUltimoCliente - clientesPorPagina;
  const currentClientes = allClientes.slice(indexPrimerCliente, indexUltimoCliente);

  const paginado = (pageNumber) => {
    setCurrentPage(pageNumber);
    localStorage.setItem('currentPage', pageNumber);
  };
  //FINAL PAGINADO

  useEffect(() => {
    dispatch(getClientes());
    const savedPage = localStorage.getItem('currentPage');
    if(savedPage){
      setCurrentPage(parseInt(savedPage, 10))
    }
  }, [dispatch]);

  function handleClick(e){
    e.preventDefault();
    dispatch(getClientes());
    setCurrentPage(1);
    localStorage.removeItem('currentPage');
  };

  function handleSort(e){
    e.preventDefault();
    dispatch(orderByName(e.target.value));
    setCurrentPage(1);
    setOrden(`Ordenado ${e.target.value}`);
    console.log(orden);
  }
  
  function handleDelete(id){
    dispatch(deleteCliente(id));
  }

  return(
    <div>
      <Link to='/cliente'><button>crear cliente</button></Link>
      <Link to='/obras-sociales'><button>ver obras sociales</button></Link>
      <h1>CLIENTES</h1>
      <button onClick={ e => {handleClick(e)} }>
        volver a cargar todos los clientes
      </button>
      <div>
        <select onChange={e => handleSort(e)} value={orden}>
          <option value={""}>--orden--</option>
          <option value='asc'>ascendente</option>
          <option value='desc'>descendente</option>
        </select>
        
        <SearchBar inHome={true} inObrasSociales={false}/>
        <Paginado
          itemsPorPagina={clientesPorPagina}
          allItems={allClientes.length}
          paginado={paginado}
        />
        {
          error ? 
          
          <p style={{ color: 'red' }}>{error}</p>
          :
          // currentClientes?.map((el) =>(
          //   <React.Fragment key={el.id}>
          //   <Link to={`/home/${el.id}`}>
          //     <Card nombre={el.nombre} apellido={el.apellido} puntos={el.puntos} />
          //   </Link>
          //     <button onClick={() => {handleDelete(el.id)}}>eliminar cliente</button>
          //   <Link to={`/cliente/${el.id}`}>
          //     <button>editar cliente</button>
          //   </Link>
          // </React.Fragment>
          // ))
          (<table border="1" cellPadding="10" cellSpacing="0">
            <thead>
              <tr>
                <th>NOMBRE</th>
                <th>APELLIDO</th>
                <th>PUNTOS</th>
                <th>D.N.I</th>
                <th>OBRA SOCIAL</th>
                <th>AFILIADO</th>
                <th>DIRECCION</th>
                <th>TELEFONO</th>
                <th>ACCIONES</th>
              </tr>
            </thead>
            <tbody>
              {currentClientes?.map((el) => (
                <tr key={el.id}>
                  <td>{el.nombre}</td>
                  <td>{el.apellido}</td>
                  <td>{el.puntos}</td>
                  <td>{el.dni}</td>
                  <td>{el.obraSocials?.map((os, index) => (
                      <div key={index}>{os.nombre.toUpperCase()}</div>
                      ))}
                  </td>
                  <td>{el.numeroDeAfiliado}</td>
                  <td>{el.direccion}</td>
                  <td>{el.telefono}</td>
                  <td>
                    <Link to={`/cliente/${el.id}`}>
                      <button>editar cliente</button>
                    </Link>
                    <button onClick={() => handleDelete(el.id)}>eliminar cliente</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>)
        }
        

      </div>
    </div>
  )

}
