import React, {useState, useEffect} from "react";
import {Link, useNavigate, useParams} from 'react-router-dom';
import { useDispatch, useSelector } from "react-redux";
import { getObrasSociales, editCliente, getClienteById } from "../redux/actions";
import CalculadoraPuntos from "./CalculadoraPuntos";


export default function ClientEdit(){

  const dispatch = useDispatch();
  const { id } = useParams();
  const navigate = useNavigate();
  const obrasSociales = useSelector((state) => state.obrasSociales);
  const [selectedObraSocial, setSelectedObraSocial] = useState("");
  const [input, setInput] = useState({
    nombre: "",
    apellido: "",
    puntos: 0,
    dni: "",
    numeroDeAfiliado: "",
    direccion: "",
    telefono: "",
    obraSocial: [],
  });

  useEffect(()=>{
    dispatch(getClienteById(id));
    dispatch(getObrasSociales());
  }, [dispatch, id]);

  const cliente = useSelector((state) => state.clientes);

useEffect(() => {
  if (cliente && cliente.length > 0) {
    setInput({
      nombre: cliente[0].nombre,
      apellido: cliente[0].apellido,
      puntos: cliente[0].puntos,
      dni: cliente[0].dni,
      numeroDeAfiliado: cliente[0].numeroDeAfiliado,
      direccion: cliente[0].direccion,
      telefono: cliente[0].telefono,
      obraSocial: cliente[0].obraSocials?.map(os => os.nombre) || [],
    });
  }
}, [cliente]);

const handleChange = (e) => {
  setInput({
    ...input,
    [e.target.name]: e.target.value,
  });
};

const handleSelectChange = (e) => {
  const selectedValue = e.target.value;
  if (selectedValue && !input.obraSocial.includes(selectedValue)) {
    setInput({
      ...input,
      obraSocial: [...input.obraSocial, selectedValue],
    });
    setSelectedObraSocial("");
  }
};

const handleSubmit = (e) => {
  e.preventDefault();
  dispatch(editCliente(input, id));
  alert("Cliente actualizado con éxito.");
  navigate("/home");
};

function handleDelete(el){
  setInput({
    ...input,
    obraSocial: input.obraSocial.filter(os => os !== el)
  })
}

function sumarPuntos(puntosSumados){
  setInput({
    ...input,
    puntos: puntosSumados
  })
}


  return (
    <div>
      <Link to={'/home'}><button>volver</button></Link>
    <h1>EDITAR CLIENTE</h1>
    <p>ID del cliente: {id}</p>
    <form onSubmit={(e) => handleSubmit(e)}>
      <div>
        <label>Nombre:</label>
        <input type="text" value={input.nombre} onChange={(e) => handleChange(e)} name="nombre"/>
      </div>
      <div>
        <label>Apellido:</label>
        <input type="text" value={input.apellido} onChange={(e) => handleChange(e)} name="apellido" />
      </div>
      <div>
        <label>Puntos:</label>
        <input type="number" value={input.puntos} onChange={(e) => handleChange(e)} name="puntos" />
      </div>
      <div>
        <label>DNI:</label>
        <input type="text" value={input.dni} onChange={(e) => handleChange(e)} name="dni" />
      </div>
      <div>
        <label>Número de Afiliado:</label>
        <input type="text" value={input.numeroDeAfiliado} onChange={(e) => handleChange(e)} name="numeroDeAfiliado" />
      </div>
      <div>
        <label>Dirección:</label>
        <input type="text" value={input.direccion} onChange={(e) => handleChange(e)} name="direccion" />
      </div>
      <div>
        <label>Teléfono:</label>
        <input type="text" value={input.telefono} onChange={(e) => handleChange(e)} name="telefono" />
      </div>
      <div>
          <label>Obras Sociales: </label>
          
          <select onChange={(e) => handleSelectChange(e)} value={selectedObraSocial}>
            <option value={""}>--elige una obra social--</option>
            {obrasSociales.map((os) =>(
              <option key={os.nombre} value={os.nombre}>{os.nombre.toUpperCase()}</option>
            ))}
          </select>
          
          {input.obraSocial.map((el, index) => 
            <div key={el + index}>
              <p>{el.toUpperCase()}</p>
              <button type="button" onClick={() => handleDelete(el)}>X</button>
            </div>
          )}
        </div>
      <button type="submit">Guardar Cambios</button>
    </form>
    <CalculadoraPuntos 
    argPuntos={input.puntos}
    sumarPuntos={sumarPuntos}/>
  </div>
  )
}
