import React, {useState, useEffect} from "react";
import { Form, FormGroup, Label, Col, Input, Button, Container, Card, Modal, ModalHeader, ModalBody, ModalFooter} from 'reactstrap';


export default function CalculadoraPuntos({argPuntos, sumarPuntos, canjearPuntos}){
  const [puntosSumados, setPuntosSumados] = useState(0);
  const [puntosTotal, setPuntosTotal] = useState(0);
  const [dineroTotal, setDineroTotal] = useState(0);
  const [puntosRestantes, setPuntosRestantes] = useState(0);
  const [calculadora, setCalculadora] = useState(false);
  const [canje, setCanje] = useState(false);
  const [modal, setModal] = useState(false);
  const [input, setInput] = useState({
    dineroPorPunto: "",
    nuevaCompra: "",
    puntoCanjeado: "",
    puntosACanjear: "",
  })
  

  function handleInputChange(e){
    const { name, value} = e.target;
    setInput({
      ...input,
      [name]: value,
    });
  }

  function handleOpenCalculator(e){
    if(e === false){
      setCalculadora(false);
    }
    if(e === true){
      setCalculadora(true)
    }
  }

  function handleCanje(e){
    if(e === false){
      setCanje(false)
    }
    if(e === true){
      setCanje(true)
    }
  }

  function limpiarInputs() {
    setInput({
      dineroPorPunto: "",
      nuevaCompra: "",
      puntoCanjeado: "",
      puntosACanjear: "",
    });
  }

  const toggle = () => setModal(!modal);

  useEffect(() => {
    const dineroPorPunto = parseFloat(input.dineroPorPunto) || 0;
    const nuevaCompra = parseFloat(input.nuevaCompra) || 0;
    const puntoCanjeado = parseFloat(input.puntoCanjeado) || 0;
    const puntosACanjear = parseFloat(input.puntosACanjear) || 0;
    const puntosActuales = parseFloat(argPuntos) || 0;

    const nuevosPuntos = dineroPorPunto > 0 ? Math.round(nuevaCompra / dineroPorPunto) : 0;
    
    setPuntosSumados(nuevosPuntos);
    setPuntosTotal(puntosActuales + nuevosPuntos);
    setDineroTotal(puntosACanjear * puntoCanjeado);
    setPuntosRestantes(puntosActuales - puntosACanjear);




  }, [input, argPuntos]);
  
  
  
  return (
    <Container>
    <div>
    <hr></hr>
      <div>
        <h1>SUMAR PUNTOS</h1>
        <Button color="success" onClick={(e) => handleOpenCalculator(e = true)}>abrir</Button>{' '}
        <Button color="danger" onClick={(e) => handleOpenCalculator(e = false)}>cerrar</Button>
      </div>
      <br/>
      { calculadora ?
      <Card color="light" className="p-2 shadow w-75">
        <br/>
       <Container> 
      <Form>
      <FormGroup row>
        <Label for='dineroPorPunto' sm={5}><h5>Dinero necesario por punto: $ </h5></Label>
        <Col sm={6}>
          <Input type="number" name="dineroPorPunto" value={input.dineroPorPunto} onChange={(e) => handleInputChange(e)}></Input>
        </Col>
      </FormGroup>
      <FormGroup row>
        <Label for='dineroPorPunto' sm={5}><h5>Monto de la nueva compra: $ </h5></Label>
        <Col sm={6}>
          <Input type="number" name="nuevaCompra" value={input.nuevaCompra} onChange={(e) => handleInputChange(e)}></Input>
        </Col>
      </FormGroup>
      <FormGroup>
        <Label for='puntosDelCliente' sm={6}><h5>Puntos del cliente: {argPuntos}</h5></Label>
      </FormGroup>
      <FormGroup>
        <Label for='puntosDelCliente' sm={6}><h5>Puntos sumados en esta compra: {puntosSumados}</h5></Label>
      </FormGroup>
      <FormGroup>
        <Label for='puntosTotal' sm={6}><h5>Puntos totales del cliente: {puntosTotal}</h5></Label>
      </FormGroup>
    
      <Button color="success" onClick={(e) => {
        const confirmar = window.confirm("¿Está seguro que quiere sumar los puntos?");
        if(confirmar) sumarPuntos(puntosTotal);  
        limpiarInputs();
        handleOpenCalculator(e = false)}} >
        sumar puntos
      </Button> 
      </Form>
      </Container>
      <br/>
      </Card> :
      <div></div>}
    </div> 
    <div>
    <hr></hr>
    <div>
        <h1>CANJEAR PUNTOS</h1>
        <Button color="success" onClick={(e) => handleCanje(e = true)}>abrir</Button>{' '}
        <Button color="danger" onClick={(e) => handleCanje(e = false)}>cerrar</Button>
      </div>
      <br/>
    { canje ?
    <Card color="light" className="p-2 shadow w-75">
    <br/>
    <Container>
    <Form>
      <FormGroup row>
        <Label for='puntoCanjeado' sm={5}><h5>Valor de punto canjeado: $</h5></Label>
        <Col sm={6}>
          <Input type="number" name="puntoCanjeado" value={input.puntoCanjeado} onChange={(e) => handleInputChange(e)}></Input>
        </Col>
      </FormGroup>
      <FormGroup row>
        <Label for='puntosACanjear' sm={5}><h5>Puntos a canjear: </h5></Label>
        <Col sm={6}>
          <Input type="number" name="puntosACanjear" value={input.puntosACanjear} onChange={(e) => handleInputChange(e)}></Input>
        </Col>
      </FormGroup>
      <FormGroup>
        <Label for='puntosDelCliente' sm={6}><h5>Puntos del cliente: {argPuntos}</h5></Label>
      </FormGroup>
      <FormGroup>
        <Label for='puntosRestantes' sm={6}><h5>Puntos restantes: {puntosRestantes}</h5></Label>
      </FormGroup>
      <FormGroup>
        <Label for='dineroTotal' sm={6}><h5>Valor de puntos totales en dinero: {dineroTotal}</h5></Label>
      </FormGroup>
      <Button color="success" onClick={(e) =>{
        const confirmar = window.confirm("¿Está seguro que quiere canjear los puntos?");
        if(confirmar) canjearPuntos(puntosRestantes); 
        limpiarInputs();
        handleCanje(e = false)}}>
          canjear puntos
      </Button> 
    </Form>
    </Container>
      <br/>
    </Card> :
    <div></div>
    } 
    </div>
    <br/>
    <br/>
    <div>
      <Button color="danger" onClick={toggle}>
        Click Me!
      </Button>
      <Modal className="w-75" isOpen={modal} toggle={toggle}>
        <ModalHeader className="w-75" toggle={toggle}>Sumar Puntos</ModalHeader>
        <ModalBody Row className="w-75">
        {
      <Card color="light" className="p-2 shadow w-75">
        <br/>
       <Container> 
      <Form>
      <FormGroup row>
        <Label for='dineroPorPunto' sm={5}><h5>Dinero necesario por punto: $ </h5></Label>
        <Col sm={6}>
          <Input type="number" name="dineroPorPunto" value={input.dineroPorPunto} onChange={(e) => handleInputChange(e)}></Input>
        </Col>
      </FormGroup>
      <FormGroup row>
        <Label for='dineroPorPunto' sm={5}><h5>Monto de la nueva compra: $ </h5></Label>
        <Col sm={6}>
          <Input type="number" name="nuevaCompra" value={input.nuevaCompra} onChange={(e) => handleInputChange(e)}></Input>
        </Col>
      </FormGroup>
      <FormGroup>
        <Label for='puntosDelCliente' sm={6}><h5>Puntos del cliente: {argPuntos}</h5></Label>
      </FormGroup>
      <FormGroup>
        <Label for='puntosDelCliente' sm={6}><h5>Puntos sumados en esta compra: {puntosSumados}</h5></Label>
      </FormGroup>
      <FormGroup>
        <Label for='puntosTotal' sm={6}><h5>Puntos totales del cliente: {puntosTotal}</h5></Label>
      </FormGroup>
    
      
      </Form>
      </Container>
      <br/>
      </Card>}
        </ModalBody>
        <ModalFooter>
        <Button color="success" onClick={(e) => {
        const confirmar = window.confirm("¿Está seguro que quiere sumar los puntos?");
        if(confirmar) sumarPuntos(puntosTotal);  
        limpiarInputs();
        
        handleOpenCalculator(e = false)}} >
        sumar puntos
      </Button> 
          <Button color="primary" onClick={toggle}>
            Do Something
          </Button>{' '}
          <Button color="secondary" onClick={toggle}>
            Cancel
          </Button>
        </ModalFooter>
      </Modal>
    </div>
    </Container>
  )
}
