import React from "react";
import { Container, Button} from "reactstrap";
import { Link } from "react-router-dom";
import LoginButton from "./LoginButton.jsx";
import Profile from "./Profile.jsx";
import LogoutButton from "./LogoutButton.jsx";
import { useAuth0 } from "@auth0/auth0-react";

export default function LandingPage(){

  const {user, isAuthenticated} = useAuth0();
  
  const usuarioPermitido = user?.email === "tomasfernandezvaldes@gmail.com" && user?.name === "Tomas Fernandez Valdes";


  return(
    <Container className="d-flex flex-column justify-content-center align-items-center vh-100 text-center">
      <h1 className="mb-4">Bienvenidos a Farmacia Alto Palermo</h1>
      
      
      {isAuthenticated && usuarioPermitido ?
      <div>
        <h3>Este usuario tiene permiso para acceder a la aplicaci√≥n:</h3>
        <Profile/>
        <Link to="/home">
          <Button className="first-button me-2" size="lg">ingresar</Button>
        </Link> 
        <LogoutButton/>
      </div>
      :
      <div>
        <LoginButton/>
        <LogoutButton/> 
      </div>
      }
    </Container>
  )
}
