import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faUser, faCreditCard } from "@fortawesome/free-solid-svg-icons";
import { Button, Nav, NavItem } from "reactstrap";
import "./NavBar.css"; // Importamos el archivo de estilos

export default function NavBar() {
  const [isMobile, setIsMobile] = useState(window.innerWidth <= 1100);

  // Manejar el cambio de tamaño de la ventana
  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth <= 1100);
    };

    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  return (
    <Nav className={`navbar ${isMobile ? "horizontal-nav" : "vertical-nav"}`}>
      <NavItem className="nav-title">
        <h5>Farmacia Alto Palermo</h5>
      </NavItem>
      <NavItem>
        <Link to="/home">
          <Button className="first-button">
            <FontAwesomeIcon icon={faUser} /> Clientes
          </Button>
        </Link>
      </NavItem>
      <NavItem>
        <Link to="/obras-sociales">
          <Button className="first-button">
            <FontAwesomeIcon icon={faCreditCard} /> Obras Sociales
          </Button>
        </Link>
      </NavItem>
    </Nav>
  );
}
