import React, { useState, useEffect} from "react";
import { Link } from 'react-router-dom';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faUser, faCreditCard } from "@fortawesome/free-solid-svg-icons";
import { Button, Card, Nav, NavItem} from 'reactstrap';


export default function NavBar(){
  const [isMobile, setIsMobile] = useState(window.innerWidth <= 1100);

  // Manejar el cambio de tamaño de la ventana
  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth <= 1100);
    };

    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  return (
      <Nav className={`pills ${isMobile ? "" : "vertical"}`}>
        <NavItem className="mb-4">
          <h5>Farmacia Alto Palermo</h5>
        </NavItem>
        <NavItem className="mb-4">
          <Link to='/home'>
            <Button className={`first-button ${isMobile ? "me-2" : "w-100"}`}>
              <FontAwesomeIcon icon={faUser} /> Clientes
            </Button>
          </Link>
        </NavItem>
        <NavItem>
          <Link to='/obras-sociales'>
            <Button color="success" className={`first-button ${isMobile ? "me-2" : "w-100"}`}>
              <FontAwesomeIcon icon={faCreditCard} /> Obras Sociales
            </Button>
          </Link>
        </NavItem>
      </Nav>
  );
}
