import React, {useState} from "react";
import {Link, useNavigate} from 'react-router-dom';
import { useDispatch } from "react-redux";
import { postObraSocial } from "../redux/actions";


function validate(input){
  let errors = {};
  if(!input.nombre) {
    errors.nombre = "se requiere un nombre";
  }
  return errors;
}

export default function ObraSocialCreate(){

  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [errors, setErrors] = useState({});
  const [input, setInput] = useState({
    nombre: '',
  });

  function handleChange(e){
    setInput({
      ...input,
      [e.target.name] : e.target.value
    })
    setErrors(validate({
      ...input,
      [e.target.name] : e.target.value
    }));
    console.log(input);
  };

  function handleSubmit(e){
    e.preventDefault();
    console.log(input);
    dispatch(postObraSocial(input));
    alert('obra social creada con éxito!');
    setInput({
      nombre: '',
    });
    navigate('/obras-sociales');
  }




  return (
    <div>
      <Link to={'/home'}><button>volver</button></Link>
      <h1>Agregar Nueva Obra Social</h1>
      <form onSubmit={(e) => handleSubmit(e)}>
        <div>
          <label>Nombre*: </label>
          <input type="text" value={input.nombre} name='nombre' onChange={(e) => handleChange(e)}/>
          {errors.nombre && (<p>{errors.nombre}</p>)}
        </div>
        {Object.keys(errors).length?
        <button type="submit" disabled>agregar obra social</button>
        :
        <button type="submit">agregar obra social</button>
      }    
        </form>
    </div>
  )
}
