import React from "react";
import { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getClientes } from "../redux/actions";
import { Link } from 'react-router-dom';
import Card from "./Card";

export default function Home(){

  const dispatch = useDispatch();
  const allClientes = useSelector((state) => state.clientes);

  useEffect(() => {
    dispatch(getClientes());
  }, [dispatch]);

  function handleClick(e){
    e.preventDefault();
    dispatch(getClientes());
  }

  return(
    <div>
      <Link to='/cliente'>Crear Cliente</Link>
      <h1>FARMACIA ALTO PALERMO</h1>
      <button onClick={ e => {handleClick(e)} }>
        Volver a cargar todos los clientes
      </button>
      <div>
        <select>
          <option value='asc'>Ascendente</option>
          <option value='desc'>Descendente</option>
        </select>
        {
          allClientes && allClientes.map(el =>{
            return(
            <Card nombre={el.nombre} apellido={el.apellido} puntos={el.puntos}/>
          )})
        }

      </div>
    </div>
  )

}
