import React, {useState, useEffect} from "react";
import {Link, useNavigate} from 'react-router-dom';
import { useDispatch, useSelector } from "react-redux";
import { postCliente, getObrasSociales } from "../redux/actions";

function validate(input){
  let errors = {};
  if(!input.nombre) {
    errors.nombre = "se requiere un nombre";
  } 
  if(!input.apellido) {
    errors.apellido = "se requiere un apellido"
  }
  if(!input.dni) {
    errors.dni = "se requiere un DNI";
  }
  return errors;
}






export default function ClientCreate(){
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const obrasSociales = useSelector((state) => state.obrasSociales);
  const [selectedObraSocial, setSelectedObraSocial] = useState("");
  const [errors, setErrors] = useState({});
  const [input, setInput] = useState({
    nombre: '',
    apellido: '',
    puntos: 0,
    dni: '',
    numeroDeAfiliado: '',
    direccion: '',
    telefono: '',
    obraSocial: []
  });

  function handleChange(e){
    setInput({
      ...input,
      [e.target.name] : e.target.value
    })
    setErrors(validate({
      ...input,
      [e.target.name] : e.target.value
    }));
    console.log(input);
  };

  function handleSelect(e){
    if(e.target.value){
      setInput({
        ...input,
        obraSocial: [...input.obraSocial, e.target.value]
      });
      setSelectedObraSocial("");
    }  
  };

  function handleDelete(el){
    setInput({
      ...input,
      obraSocial: input.obraSocial.filter(os => os !== el)
    })
  }

  function handleSubmit(e){
    e.preventDefault();
    console.log(input);
    dispatch(postCliente(input));
    alert('cliente creado con Ã©xito!');
    setInput({
      nombre: '',
      apellido: '',
      puntos: 0,
      dni: '',
      numeroDeAfiliado: '',
      direccion: '',
      telefono: '',
      obraSocial: []
    });
    navigate('/home');
  }

  useEffect(()=>{
    dispatch(getObrasSociales());
    
  }, [dispatch]);

  return(
    <div>
      <Link to={'/home'}><button>volver</button></Link>
      <h1>Agregar Nuevo Cliente</h1>
      <form onSubmit={(e) => handleSubmit(e)} onKeyDown={(e) => e.key === 'Enter' && e.preventDefault()}>
        <div>
          <label>Nombre*: </label>
          <input type="text" value={input.nombre} name='nombre' onChange={(e) => handleChange(e)}/>
          {errors.nombre && (<p>{errors.nombre}</p>)}
        </div>
        <div>
          <label>Apellido*: </label>
          <input type="text" value={input.apellido} name='apellido' onChange={(e) => handleChange(e)}/>
          {errors.apellido && (<p>{errors.apellido}</p>)}
        </div>
        <div>
          <label>Puntos: </label>
          <input type="number" value={input.puntos} name='puntos' onChange={(e) => handleChange(e)}/>
        </div>
        <div>
          <label>D.N.I*: </label>
          <input type="text" value={input.dni} name='dni' onChange={(e) => handleChange(e)}/>
          {errors.dni && (<p>{errors.dni}</p>)}
        </div>
        <div>
          <label>NÃºmero de Afiliado: </label>
          <input type="text" value={input.numeroDeAfiliado} name='numeroDeAfiliado' onChange={(e) => handleChange(e)}/>
        </div>
        <div>
          <label>DirecciÃ³n: </label>
          <input type="text" value={input.direccion} name='direccion' onChange={(e) => handleChange(e)}/>
        </div>
        <div>
          <label>TelÃ©fono: </label>
          <input type="text" value={input.telefono} name='telefono' onChange={(e) => handleChange(e)}/>
        </div>
        <div>
          <label>Obras Sociales: </label>
          
          <select onChange={(e) => handleSelect(e)} value={selectedObraSocial}>
            <option value={""}>--elige una obra social--</option>
            {obrasSociales.map((os) =>(
              <option key={os.nombre} value={os.nombre}>{os.nombre.toUpperCase()}</option>
            ))}
          </select>
          
          {input.obraSocial.map((el, index) => 
            <div key={el + index}>
              <p>{el.toUpperCase()}</p>
              <button onClick={() => handleDelete(el)}>X</button>
            </div>
          )}
        </div>
        {Object.keys(errors).length?
        <button type="submit" disabled>agregar cliente</button>
        :
        <button type="submit">agregar cliente</button>
      }    

      </form>

      
    </div>
  )
}
