import React from "react";
import { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getClientes, orderByName, deleteCliente } from "../redux/actions";
import { Link } from 'react-router-dom';
// import Card from "./Card";
import Paginado from "./Paginado";
import SearchBar from "./SearchBar";
import 'bootstrap/dist/css/bootstrap.min.css';
import { Table, Button, Container, Dropdown, DropdownToggle, Card, DropdownMenu, DropdownItem, Alert, Nav, NavItem, Col, Row } from 'reactstrap';

export default function Home(){

  const dispatch = useDispatch();
  const allClientes = useSelector((state) => state.clientes);
  const error = useSelector((state) => state.error);
  const [orden, setOrden] = useState('ordenar');
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const toggleDropdown = () => setDropdownOpen(!dropdownOpen);
  //PAGINADO
  const [currentPage, setCurrentPage] = useState(1);
  const [clientesPorPagina, /*setClientesPorPagina*/] = useState(6);
  const indexUltimoCliente = currentPage * clientesPorPagina;
  const indexPrimerCliente = indexUltimoCliente - clientesPorPagina;
  const currentClientes = allClientes.slice(indexPrimerCliente, indexUltimoCliente);

  const paginado = (pageNumber) => {
    setCurrentPage(pageNumber);
    localStorage.setItem('currentPage', pageNumber);
  };
  //FINAL PAGINADO

  useEffect(() => {
    dispatch(getClientes());
    const savedPage = localStorage.getItem('currentPage');
    if(savedPage){
      setCurrentPage(parseInt(savedPage, 10))
    }
  }, [dispatch]);

  function handleClick(e){
    e.preventDefault();
    dispatch(getClientes());
    setCurrentPage(1);
    localStorage.removeItem('currentPage');
  };

  function handleSort(e){
    //e.preventDefault();
    dispatch(orderByName(e.target.value));
    setCurrentPage(1);
    setOrden(`Ordenado ${e.target.value}`);
    console.log(orden);
  }
  
  function handleDelete(id){
    dispatch(deleteCliente(id));
  }

  return(
    <>
  
    
    <br/>
    <Container>
    <Row>
    {/* <Col sm={2} className="p-1">
    <br/>
    <br/>
    <Card color="light"  style={{width: '8rem'}} className="vh-100 p-3">
      <h5>Farmacia Alto Palermo</h5>
    <br/>
    <Nav vertical>
      <NavItem>
        <Link to='/home'><Button color="primary" className="pe-4 ">clientes</Button></Link>
      </NavItem>
      <br/>
      <NavItem>
        <Link to='/obras-sociales'><Button color="primary">obras sociales</Button></Link>
      </NavItem>
    </Nav>
    </Card>
    </Col> */}
    <Col sm={10}>
      <br/>
      <br/>
      <h2>CLIENTES</h2>
      <br/>
      <SearchBar inHome={true} inObrasSociales={false}/>
      <br/>
      <Row>
        <Col sm={2}>
          <Link to='/cliente'><Button color="primary">crear cliente</Button></Link> {' '}
        </Col>
        <Col sm={3}>
        <Dropdown isOpen={dropdownOpen} toggle={toggleDropdown} className="d-inline-block">
          <DropdownToggle caret color="primary"> ordenar </DropdownToggle>
          <DropdownMenu>
            <DropdownItem onClick={() => handleSort({ target: { value: 'asc' } })}>
              Ascendente
            </DropdownItem>
            <DropdownItem onClick={() => handleSort({ target: { value: 'desc' } })}>
              Descendente
            </DropdownItem>
          </DropdownMenu>
        </Dropdown>
        </Col>
        <Col sm={3}>
        <Paginado
          itemsPorPagina={clientesPorPagina}
          allItems={allClientes.length}
          paginado={paginado}
          />
        </Col>
        <Col sm={4}>
          <Button color="success" onClick={ e => {handleClick(e)} }> volver a cargar todos los clientes</Button>
        </Col>
        </Row>
        <br/>
        
        {
          error ? 
          
          <Alert color="danger">{error}</Alert>
          :
          (
            
          <Table bordered>
            <thead className="table-light">
              <tr>
                <th>NOMBRE</th>
                <th>APELLIDO</th>
                <th>PUNTOS</th>
                <th>D.N.I</th>
                <th>OBRA SOCIAL</th>
                <th>AFILIADO</th>
                <th>DIRECCION</th>
                <th>TELEFONO</th>
                <th>ACCIONES</th>
              </tr>
            </thead>
            <tbody className="table-light">
              {currentClientes?.map((el) => (
                <tr key={el.id}>
                  <td>{el.nombre}</td>
                  <td>{el.apellido}</td>
                  <td>{el.puntos}</td>
                  <td>{el.dni}</td>
                  <td>{el.obraSocials?.map((os, index) => (
                      <div key={index}>{os.nombre.toUpperCase()}</div>
                      ))}
                  </td>
                  <td>{el.numeroDeAfiliado}</td>
                  <td>{el.direccion}</td>
                  <td>{el.telefono}</td>
                  <td>
                    <Link to={`/cliente/${el.id}`}>
                      <Button color="info" size="sm" className="mb-2 pe-4">editar</Button>
                    </Link>
                    <Button color="danger" size="sm" onClick={() => handleDelete(el.id)}>eliminar</Button>
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>)
        }
      </Col>
      </Row>
      </Container>
    </>
  )

}
