import React, {useState, useEffect} from "react";
import {Link, useHistory} from 'react-router-dom';
import { useDispatch, useSelector } from "react-redux";
import { postCliente, getObrasSociales } from "../redux/actions";

export default function ClientCreate(){
  const dispatch = useDispatch();
  const obrasSociales = useSelector((state) => state.obrasSociales);

  const [input, setInput] = useState({
    nombre: '',
    apellido: '',
    puntos: 0,
    dni: '',
    numeroDeAfiliado: 0,
    direccion: '',
    telefono: '',
    obraSocial: []
  });

  function handleChange(e){
    setInput({
      ...input,
      [e.target.name] : e.target.value
    })
    console.log(input);
  }

  useEffect(()=>{
    dispatch(getObrasSociales());
  }, [dispatch]);

  return(
    <div>
      <Link to={'/home'}><button>volver</button></Link>
      <h1>Agregar Nuevo Cliente</h1>
      <form>
        <div>
          <label>Nombre: </label>
          <input type="text" value={input.nombre} name='nombre'/>
        </div>
        <div>
          <label>Apellido: </label>
          <input type="text" value={input.apellido} name='apellido'/>
        </div>
        <div>
          <label>Puntos: </label>
          <input type="number" value={input.puntos} name='puntos'/>
        </div>
        <div>
          <label>D.N.I: </label>
          <input type="text" value={input.dni} name='dni'/>
        </div>
        <div>
          <label>Número de Afiliado: </label>
          <input type="number" value={input.numeroDeAfiliado} name='numeroDeAfiliado'/>
        </div>
        <div>
          <label>Dirección: </label>
          <input type="text" value={input.direccion} name='direccion'/>
        </div>
        <div>
          <label>Teléfono: </label>
          <input type="text" value={input.telefono} name='telefono'/>
        </div>
        <div>
          <label>Obras Sociales: </label>
          <select>
            {obrasSociales.map((os) =>(
              <option value={os.nombre}>{os.nombre.toUpperCase()}</option>
            ))}
          </select>
        </div>

        <button type="submit">agregar cliente</button>

      </form>
    </div>
  )
}
