import React from "react";
import { useState} from 'react';
import { useDispatch } from "react-redux";
import { getNombreCliente } from "../redux/actions";

export default function SearchBar({inHome}){
  const dispatch = useDispatch();
  const [nombre, setNombre] = useState('');


  function handleInputChange(e){
    e.preventDefault();
    setNombre(e.target.value);
    console.log(nombre);
  };

  function handleDelete(){
    setNombre('')
  }

  function handleSubmit(e){
    e.preventDefault();
    if(inHome === true){
      dispatch(getNombreCliente(nombre));
    }
    
  };

  function handleOnEnter(e){
    if(e.key === "Enter"){
      handleSubmit(e)
    }
  }

  return(
    <div>
      <input type="text" placeholder="buscar..." value={nombre} onChange={handleInputChange} onKeyDown={handleOnEnter}/>
      <button type="button" onClick={handleDelete}>X</button>
      <button type="submit" onClick={(e) => handleSubmit(e)}>Buscar</button>
    </div>
  )
}
